mapper.update:
UPDATE DOSIRAK
SET IS_CUSTOM = 'NORMAL',
UPDATED_AT = SYSTIMESTAMP,
UPDATED_BY = #{adminId}
WHERE ID = #{customDosirakId}

mapper.select:
WITH dates AS ( SELECT TO_CHAR(TRUNC(SYSDATE) - LEVEL + 1, 'YYYY-MM-DD') AS sales_date FROM dual CONNECT BY LEVEL <= 7 ), sales AS ( SELECT TO_CHAR(TRUNC(o.arrival_at), 'YYYY-MM-DD') AS sales_date, COUNT(*) AS count FROM orders o JOIN order_items oi ON o.id = oi.order_id WHERE o.arrival_at >= TRUNC(SYSDATE) - 6
AND oi.dosirak_id = #{dosirakId}
GROUP BY TO_CHAR(TRUNC(o.arrival_at), 'YYYY-MM-DD') ) SELECT d.sales_date AS day, NVL(s.count, 0) AS count FROM dates d LEFT JOIN sales s ON d.sales_date = s.sales_date ORDER BY d.sales_date

SELECT base.dosirakId, base.name, base.imageUrl, NVL(s.count, 0) AS count, RANK() OVER (ORDER BY NVL(s.count, 0) DESC) AS rank FROM ( SELECT d.id AS dosirakId, d.name, di.image_url AS imageUrl FROM dosirak d LEFT JOIN dosirak_image di ON d.id = di.dosirak_id AND di.image_type = 'MAIN' AND di.sort_order = ( SELECT MIN(sort_order) FROM dosirak_image WHERE dosirak_id = d.id AND image_type = 'MAIN' ) WHERE d.is_custom = 'NORMAL' AND ROWNUM <= 9 ) base LEFT JOIN ( SELECT oi.dosirak_id, COUNT(*) AS count FROM order_items oi JOIN orders o ON oi.order_id = o.id JOIN member m ON o.member_id = m.id
AND FLOOR(MONTHS_BETWEEN(SYSDATE, m.birthdate) / 12) BETWEEN #{ageMin} AND #{ageMax}
GROUP BY oi.dosirak_id ) s ON base.dosirakId = s.dosirak_id ORDER BY rank

SELECT o.IS_GONGGOO AS orderType, COUNT(*) AS count FROM ORDERS o JOIN ORDER_ITEMS oi ON o.ID = oi.ORDER_ID
AND oi.DOSIRAK_ID = #{dosirakId}
GROUP BY o.IS_GONGGOO
